        $this->wpSrc = $config['src'];
        $this->wpSalt = $config['salt'];
        $this->wpPhpVersion = $config['php_version'];
        $this->wpFtp = empty($config['ftp']) ? null : $config['ftp'];
        $this->wpUploadDir = empty($config['upload_dir']) ? 'wp-content/uploads' : $config['upload_dir'];
        $this->clacksoverhead = $config['clacks_overhead'];
    }

    public function checkSystem()
    {
        $error = null;

        if ($this->isApache() === false) {
            $error[] = 'Apache Server is required.';
        }
        if ($this->checkPhpVersion() === false) {
            $error[] = 'PHP <strong>' . $this->wpPhpVersion . '</strong> or higher is <a href="https://wordpress.org/about/requirements/" target="_blank">required</a>.';
        }
        if ($this->hasCurl() === false) {
            $error[] = 'Curl extension is required and not loaded.';
        }
        if ($this->isWritable() === false) {
            $error[] = 'Directory write permissions are required...<br>Change the permissions and <a href="javascript:location.reload();">reload</a> this page.';
        }

        return $error === null ? null : implode('<br><br>', $error);
    }

    public function isApache()
    {
        if (strpos($_SERVER['SERVER_SOFTWARE'], 'Apache') === false) {
            return false;
        } else {
            return true;
        }
    }

    public function checkPhpVersion()
    {
        if (version_compare(PHP_VERSION, $this->wpPhpVersion, '>=')) {
            return true;
        } else {
            return false;
        }
    }

    public function hasCurl()
    {
        if (extension_loaded('curl')) {
            return true;
        } else {
            return false;
        }
    }

    public function isWritable()
    {
        return is_writable(__DIR__);
    }

    public function chmod($path, $recursive = false, $mod = array(0666, 0777))
    {
        chmod($path, is_file($path) ? $mod[0] : $mod[1]);
        if ($recursive === true) {
            $dir = new DirectoryIterator($path);
            foreach ($dir as $item) {
                if ($item->isDir() && !$item->isDot()) {
                    chmod($item->getPathname(), $mod[1]);
                    $this->chmod($item->getPathname(), true, $mod);
                } elseif ($item->isFile()) {
                    chmod($item->getPathname(), $mod[0]);
                }
            }
        }
    }

    public function chown($path, $recursive = false, $user = null, $group = null)
    {
        global $config;
        if ($user === null && empty($config['file']['user']) === false) {
            $user = $config['file']['user'];
        }
        if ($group === null && empty($config['file']['group']) === false) {
            $group = $config['file']['group'];
        }
        if ($user === null && $group === null) {
            return null;
        }
        if ($group !== null) {
            chgrp($path, $group);
        }
        if ($user !== null) {
            chown($path, $user);
        }
        if ($recursive === true) {
            $dir = new DirectoryIterator($path);
            foreach ($dir as $item) {
                if ($item->isDir() && !$item->isDot()) {
                    $this->chown($item->getPathname(), true, $user, $group);
                    if ($group !== null) {
                        chgrp($item->getPathname(), $group);
                    }
                    if ($user !== null) {
                        chown($item->getPathname(), $user);
                    }
                } elseif ($item->isFile()) {
                    if ($group !== null) {
                        chgrp($item->getPathname(), $group);
                    }
                    if ($user !== null) {
                        chown($item->getPathname(), $user);
                    }
                }
            }
        }
    }

    public function getDownloadLinkArray()
    {
        $wpContent = file_get_contents( 'https://wordpress.org/download/release-archive/' );
        preg_match_all( "/<a href='(https:\/\/wordpress\.org\/wordpress\-([0-9\.]+)\.zip)'>zip<\/a>/", $wpContent, $matches, PREG_SET_ORDER );
        $array = array();
        foreach ( $matches as $data ) {
            $array[ $data[2] ] = $data[1];
        }

        return $array;
    }

    public function download($file, $zip = './tmp.zip')
    {
        file_put_contents($zip, file_get_contents($file));
    }

    public function downloadWordpress($lang = 'latest')
    {
        if (isset($this->wpSrc[$lang]) === true) {
            $file = $this->wpSrc[$lang];
        } else {
            $file = $this->wpSrc['latest'];
        }
        $this->download($file, './wp.zip');
    }

    /**
     * @see http://php.net/manual/en/class.ziparchive.php
     */
    public function unzip($file = './wp.zip', $path = './')
    {
        $zip = new ZipArchive;
        $res = $zip->open($file);
        if ($res === true) {
            $zip->extractTo($path);
            $zip->close();
        }
    }

    public function unlink($file = './wp.zip')
    {
        unlink($file);
    }

